import React, { Component } from 'react';
import Base from './base';
import { Connect, connect } from 'react-redux';

class Calculator extends Component {
    state = {

    };
    render() { 
        return (
            <React.Fragment>
                <Base>
                    <div className='calculator'>
                        <div className="output">
                            <div className="last-output">
                                {this.props.lastOperand} {this.props.operation}
                            </div>
                            <div className="current-output">
                                {this.props.currentOperand}
                            </div>
                        </div>
                        <div className="input">
                            <button className='deep_color'>AC</button>
                            <button className='deep_color'>x^2</button>
                            <button className='deep_color'>/</button>
                            <button className='deep_color'>Del</button>
                            <button className='common'>7</button>
                            <button className='common'>8</button>
                            <button className='common'>9</button>
                            <button className='deep_color'>*</button>
                            <button className='common'>4</button>
                            <button className='common'>5</button>
                            <button className='common'>6</button>
                            <button className='deep_color'>-</button>
                            <button className='common'>1</button>
                            <button className='common'>2</button>
                            <button className='common'>3</button>
                            <button className='deep_color'>+</button>
                            <button className='common'>+/-</button>
                            <button className='common'>0</button>
                            <button className='common'>.</button>
                            <button id='equal'>=</button>
                        </div>
                    </div>
                </Base>
            </React.Fragment>
        );
    }
}

const mapStateToProps = (state, props) => {
    return {
        currentOperand: state.currentOperand,
        lastOperand: state.lastOperand,
        operation: state.operation
    }
}
 
export default connect(mapStateToProps)(Calculator);
